  --shadow-lg: 0px 20px 25px -5px rgba(0, 0, 0, 0.4), 0px 8px 10px -6px rgba(0, 0, 0, 0.4);
  --shadow-xl: 0px 25px 50px -12px rgba(0, 0, 0, 0.5);
