import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Card, CardContent } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import {
  Heart,
  MessageCircle,
  Bookmark,
  Share2,
  ArrowLeft,
  User,
  Calendar,
  Clock,
  Edit
} from "lucide-react";

interface PostDetailProps {
  post: {
    id: string;
    title: string;
    content: string;
    coverImage?: string;
    tags: string[];
    publishedAt: string;
    readTime: number;
    author: {
      id: string;
      name: string;
      avatar?: string;
      bio?: string;
    };
    stats: {
      likes: number;
      comments: number;
    };
    isOwnPost?: boolean;
  };
  relatedPosts?: Array<{
    id: string;
    title: string;
    excerpt: string;
    coverImage?: string;
    author: {
      name: string;
      avatar?: string;
    };
    readTime: number;
  }>;
  onBack?: () => void;
  onEdit?: () => void;
  onAuthorClick?: (authorId: string) => void;
  onTagClick?: (tag: string) => void;
  onRelatedPostClick?: (postId: string) => void;
}

export default function PostDetail({
  post,
  relatedPosts = [],
  onBack,
  onEdit,
  onAuthorClick,
  onTagClick,
  onRelatedPostClick
}: PostDetailProps) {
  const [isLiked, setIsLiked] = useState(false);
  const [isSaved, setIsSaved] = useState(false);
  const [likeCount, setLikeCount] = useState(post.stats.likes);

  const handleLike = () => {
    setIsLiked(!isLiked);
    setLikeCount(prev => isLiked ? prev - 1 : prev + 1);
    console.log(`Post ${post.id} ${isLiked ? 'unliked' : 'liked'}`);
  };

  const handleSave = () => {
    setIsSaved(!isSaved);
    console.log(`Post ${post.id} ${isSaved ? 'unsaved' : 'saved'}`);
  };

  const handleShare = () => {
    console.log(`Sharing post ${post.id}`);
    // In a real app, this would copy the URL or open share dialog
    alert('Link copied to clipboard!');
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatContent = (content: string) => {
    // Simple markdown-like formatting for demo
    return content
      .split('\n')
      .map((line, index) => {
        if (line.startsWith('## ')) {
          return <h2 key={index} className="text-2xl font-bold mt-8 mb-4">{line.substring(3)}</h2>;
        }
        if (line.startsWith('### ')) {
          return <h3 key={index} className="text-xl font-semibold mt-6 mb-3">{line.substring(4)}</h3>;
        }
        if (line.startsWith('- ')) {
          return (
            <li key={index} className="ml-6 list-disc mb-2">
              {line.substring(2).replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')}
            </li>
          );
        }
        if (line.trim() === '') {
          return <br key={index} />;
        }
        return (
          <p key={index} className="mb-4 leading-relaxed text-lg">
            {line.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')}
          </p>
        );
      });
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        {/* Back Button */}
        <Button
          variant="ghost"
          onClick={() => {
            onBack?.();
            console.log('Back button clicked');
          }}
          className="mb-6"
          data-testid="button-back"
        >
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to posts
        </Button>

        {/* Article */}
        <article className="space-y-8">
          {/* Header */}
          <header className="space-y-6">
            {/* Cover Image */}
            {post.coverImage && (
              <div className="aspect-[16/9] overflow-hidden rounded-lg">
                <img
                  src={post.coverImage}
                  alt={post.title}
                  className="w-full h-full object-cover"
                  data-testid="img-post-cover"
                />
              </div>
            )}

            {/* Title */}
            <h1 className="text-4xl md:text-5xl font-bold leading-tight" data-testid="text-post-title">
              {post.title}
            </h1>

            {/* Meta and Author Info */}
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
              <div 
                className="flex items-center space-x-3 cursor-pointer hover-elevate rounded p-2 -m-2"
                onClick={() => {
                  onAuthorClick?.(post.author.id);
                  console.log(`Author clicked: ${post.author.name}`);
                }}
                data-testid="author-info"
              >
                <Avatar className="h-12 w-12">
                  <AvatarImage src={post.author.avatar} alt={post.author.name} />
                  <AvatarFallback>
                    {post.author.name.charAt(0) || <User className="h-4 w-4" />}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <p className="font-semibold text-lg">{post.author.name}</p>
                  {post.author.bio && (
                    <p className="text-muted-foreground text-sm">{post.author.bio}</p>
                  )}
                </div>
              </div>

              {/* Actions */}
              <div className="flex items-center space-x-2">
                {post.isOwnPost && (
                  <Button
                    variant="outline"
                    onClick={() => {
                      onEdit?.();
                      console.log('Edit post clicked');
                    }}
                    data-testid="button-edit-post"
                  >
                    <Edit className="h-4 w-4 mr-2" />
                    Edit
                  </Button>
                )}
              </div>
            </div>

            {/* Meta Information */}
            <div className="flex flex-wrap items-center gap-4 text-muted-foreground">
              <div className="flex items-center gap-1">
                <Calendar className="h-4 w-4" />
                <span>{formatDate(post.publishedAt)}</span>
              </div>
              <div className="flex items-center gap-1">
                <Clock className="h-4 w-4" />
                <span>{post.readTime} min read</span>
              </div>
            </div>

            {/* Tags */}
            {post.tags.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {post.tags.map((tag) => (
                  <Badge
                    key={tag}
                    variant="secondary"
                    className="cursor-pointer hover-elevate"
                    onClick={() => {
                      onTagClick?.(tag);
                      console.log(`Tag clicked: ${tag}`);
                    }}
                    data-testid={`badge-tag-${tag}`}
                  >
                    {tag}
                  </Badge>
                ))}
              </div>
            )}
          </header>

          <Separator />

          {/* Content */}
          <div className="prose prose-lg max-w-none" data-testid="post-content">
            {formatContent(post.content)}
          </div>

          <Separator />

          {/* Engagement Actions */}
          <div className="flex items-center justify-between py-4">
            <div className="flex items-center space-x-6">
              <Button
                variant="ghost"
                onClick={handleLike}
                className={`${isLiked ? 'text-red-500' : 'text-muted-foreground'} hover:text-red-500`}
                data-testid="button-like"
              >
                <Heart className={`h-5 w-5 mr-2 ${isLiked ? 'fill-current' : ''}`} />
                <span>{likeCount} likes</span>
              </Button>

              <Button
                variant="ghost"
                className="text-muted-foreground"
                onClick={() => console.log('Comments clicked')}
                data-testid="button-comments"
              >
                <MessageCircle className="h-5 w-5 mr-2" />
                <span>{post.stats.comments} comments</span>
              </Button>
            </div>

            <div className="flex items-center space-x-2">
              <Button
                variant="ghost"
                size="icon"
                onClick={handleSave}
                className={`${isSaved ? 'text-primary' : 'text-muted-foreground'}`}
                data-testid="button-save"
              >
                <Bookmark className={`h-5 w-5 ${isSaved ? 'fill-current' : ''}`} />
              </Button>

              <Button
                variant="ghost"
                size="icon"
                onClick={handleShare}
                className="text-muted-foreground"
                data-testid="button-share"
              >
                <Share2 className="h-5 w-5" />
              </Button>
            </div>
          </div>
        </article>

        {/* Related Posts */}
        {relatedPosts.length > 0 && (
          <section className="mt-16">
            <h2 className="text-2xl font-bold mb-6">Related Posts</h2>
            <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
              {relatedPosts.map((relatedPost) => (
                <Card 
                  key={relatedPost.id}
                  className="hover-elevate cursor-pointer"
                  onClick={() => {
                    onRelatedPostClick?.(relatedPost.id);
                    console.log(`Related post clicked: ${relatedPost.id}`);
                  }}
                  data-testid={`card-related-${relatedPost.id}`}
                >
                  {relatedPost.coverImage && (
                    <div className="aspect-[16/9] overflow-hidden rounded-t-lg">
                      <img
                        src={relatedPost.coverImage}
                        alt={relatedPost.title}
                        className="w-full h-full object-cover"
                      />
                    </div>
                  )}
                  <CardContent className="p-4">
                    <h3 className="font-semibold mb-2 line-clamp-2">{relatedPost.title}</h3>
                    <p className="text-muted-foreground text-sm mb-3 line-clamp-2">
                      {relatedPost.excerpt}
                    </p>
                    <div className="flex items-center justify-between text-xs text-muted-foreground">
                      <div className="flex items-center space-x-2">
                        <Avatar className="h-4 w-4">
                          <AvatarImage src={relatedPost.author.avatar} alt={relatedPost.author.name} />
                          <AvatarFallback className="text-xs">
                            {relatedPost.author.name.charAt(0)}
                          </AvatarFallback>
                        </Avatar>
                        <span>{relatedPost.author.name}</span>
                      </div>
                      <span>{relatedPost.readTime} min read</span>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </section>
        )}
      </div>
    </div>
  );
}

client/src/components/PostEditor.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Separator } from "@/components/ui/separator";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Bold,
  Italic,
  Underline,
  List,
  ListOrdered,
  Link,
  Image,
  Save,
  Eye,
  Send,
  X,
  Plus
} from "lucide-react";

interface PostEditorProps {
  initialData?: {
    title: string;
    content: string;
    excerpt: string;
    tags: string[];
    coverImage?: string;
    status: "draft" | "published";
  };
  onSave?: (data: any) => void;
  onPublish?: (data: any) => void;
  onPreview?: (data: any) => void;
  isLoading?: boolean;
}

export default function PostEditor({
  initialData,
  onSave,
  onPublish,
  onPreview,
  isLoading = false
}: PostEditorProps) {
  const [formData, setFormData] = useState({
    title: initialData?.title || "",
    content: initialData?.content || "",
    excerpt: initialData?.excerpt || "",
    tags: initialData?.tags || [],
    coverImage: initialData?.coverImage || "",
    status: initialData?.status || "draft" as "draft" | "published"
  });

  const [newTag, setNewTag] = useState("");
  const [isPublished, setIsPublished] = useState(formData.status === "published");

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const addTag = () => {
    if (newTag.trim() && !formData.tags.includes(newTag.trim())) {
      setFormData(prev => ({
        ...prev,
        tags: [...prev.tags, newTag.trim()]
      }));
      setNewTag("");
      console.log('Tag added:', newTag.trim());
    }
  };

  const removeTag = (tagToRemove: string) => {
    setFormData(prev => ({
      ...prev,
      tags: prev.tags.filter(tag => tag !== tagToRemove)
    }));
    console.log('Tag removed:', tagToRemove);
  };

  const handleSave = () => {
    const dataToSave = {
      ...formData,
      status: "draft" as const
    };
    onSave?.(dataToSave);
    console.log('Post saved as draft:', dataToSave);
  };

  const handlePublish = () => {
    const dataToPublish = {
      ...formData,
      status: "published" as const
    };
    onPublish?.(dataToPublish);
    console.log('Post published:', dataToPublish);
  };

  const handlePreview = () => {
    onPreview?.(formData);
    console.log('Preview requested:', formData);
  };

  const toolbarButtons = [
    { icon: Bold, label: "Bold", action: () => console.log('Bold formatting') },
    { icon: Italic, label: "Italic", action: () => console.log('Italic formatting') },
    { icon: Underline, label: "Underline", action: () => console.log('Underline formatting') },
    { icon: List, label: "Bullet List", action: () => console.log('Bullet list') },
    { icon: ListOrdered, label: "Numbered List", action: () => console.log('Numbered list') },
    { icon: Link, label: "Insert Link", action: () => console.log('Insert link') },
    { icon: Image, label: "Insert Image", action: () => console.log('Insert image') },
  ];

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold">
            {initialData ? 'Edit Post' : 'Create New Post'}
          </h1>

          <div className="flex items-center space-x-2">
            <Button
              variant="ghost"
              onClick={handlePreview}
              disabled={isLoading}
              data-testid="button-preview"
            >
              <Eye className="h-4 w-4 mr-2" />
              Preview
            </Button>

            <Button
              variant="outline"
              onClick={handleSave}
              disabled={isLoading}
              data-testid="button-save-draft"
            >
              <Save className="h-4 w-4 mr-2" />
              Save Draft
            </Button>

            <Button
              onClick={handlePublish}
              disabled={isLoading || !formData.title || !formData.content}
              data-testid="button-publish"
            >
              <Send className="h-4 w-4 mr-2" />
              Publish
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Editor */}
          <div className="lg:col-span-2 space-y-6">
            {/* Title */}
            <div className="space-y-2">
              <Input
                data-testid="input-title"
                placeholder="Enter your post title..."
                value={formData.title}
                onChange={(e) => handleInputChange('title', e.target.value)}
                className="text-2xl font-bold border-none px-0 placeholder:text-muted-foreground focus-visible:ring-0"
                disabled={isLoading}
              />
            </div>

            {/* Cover Image */}
            <div className="space-y-2">
              <Label htmlFor="coverImage">Cover Image URL</Label>
              <Input
                id="coverImage"
                data-testid="input-cover-image"
                placeholder="https://example.com/image.jpg"
                value={formData.coverImage}
                onChange={(e) => handleInputChange('coverImage', e.target.value)}
                disabled={isLoading}
              />
              {formData.coverImage && (
                <div className="aspect-[16/9] overflow-hidden rounded-md border">
                  <img
                    src={formData.coverImage}
                    alt="Cover preview"
                    className="w-full h-full object-cover"
                  />
                </div>
              )}
            </div>

            {/* Content Editor */}
            <Card>
              <CardHeader className="pb-3">
                {/* Toolbar */}
                <div className="flex items-center space-x-1">
                  {toolbarButtons.map((button, index) => (
                    <Button
                      key={index}
                      variant="ghost"
                      size="sm"
                      onClick={button.action}
                      className="h-8 w-8 p-0"
                      data-testid={`button-${button.label.toLowerCase().replace(' ', '-')}`}
                    >
                      <button.icon className="h-4 w-4" />
                    </Button>
                  ))}
                </div>
                <Separator />
              </CardHeader>

              <CardContent>
                <Textarea
                  data-testid="textarea-content"
                  placeholder="Start writing your story..."
                  value={formData.content}
                  onChange={(e) => handleInputChange('content', e.target.value)}
                  className="min-h-[400px] border-none resize-none focus-visible:ring-0 text-base leading-relaxed"
                  disabled={isLoading}
                />
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Publish Settings */}
            <Card>
              <CardHeader>
                <h3 className="text-lg font-semibold">Publish Settings</h3>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <Label htmlFor="publish-toggle">Publish immediately</Label>
                  <Switch
                    id="publish-toggle"
                    checked={isPublished}
                    onCheckedChange={setIsPublished}
                    data-testid="switch-publish"
                  />
                </div>

                <div className="space-y-2">
                  <Label>Status</Label>
                  <Select value={formData.status} onValueChange={(value) => 
                    handleInputChange('status', value)
                  }>
                    <SelectTrigger data-testid="select-status">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="draft">Draft</SelectItem>
                      <SelectItem value="published">Published</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </CardContent>
            </Card>

            {/* Tags */}
            <Card>
              <CardHeader>
                <h3 className="text-lg font-semibold">Tags</h3>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex space-x-2">
                  <Input
                    data-testid="input-new-tag"
                    placeholder="Add a tag..."
                    value={newTag}
                    onChange={(e) => setNewTag(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && addTag()}
                    disabled={isLoading}
                  />
                  <Button
                    size="icon"
                    onClick={addTag}
                    disabled={!newTag.trim()}
                    data-testid="button-add-tag"
                  >
                    <Plus className="h-4 w-4" />
                  </Button>
                </div>

                <div className="flex flex-wrap gap-2">
                  {formData.tags.map((tag) => (
                    <Badge
                      key={tag}
                      variant="secondary"
                      className="flex items-center gap-1"
                    >
                      {tag}
                      <button
                        onClick={() => removeTag(tag)}
                        className="ml-1 hover:text-destructive"
                        data-testid={`button-remove-tag-${tag}`}
                      >
                        <X className="h-3 w-3" />
                      </button>
                    </Badge>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Excerpt */}
            <Card>
              <CardHeader>
                <h3 className="text-lg font-semibold">Excerpt</h3>
              </CardHeader>
              <CardContent>
                <Textarea
                  data-testid="textarea-excerpt"
                  placeholder="Write a brief description of your post..."
                  value={formData.excerpt}
                  onChange={(e) => handleInputChange('excerpt', e.target.value)}
                  className="min-h-[100px] resize-none"
                  disabled={isLoading}
                />
                <p className="text-xs text-muted-foreground mt-2">
                  {formData.excerpt.length}/160 characters
                </p>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/PostFeed.tsx.content:
import { useState } from "react";
import PostCard from "./PostCard";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Search, Filter, TrendingUp, Clock, Heart } from "lucide-react";

interface Post {
  id: string;
  title: string;
  excerpt: string;
  coverImage?: string;
  tags: string[];
  publishedAt: string;
  readTime: number;
  author: {
    name: string;
    avatar?: string;
  };
  stats: {
    likes: number;
    comments: number;
  };
}

interface PostFeedProps {
  posts: Post[];
  onPostClick?: (postId: string) => void;
  onAuthorClick?: (authorName: string) => void;
  onTagClick?: (tag: string) => void;
  onLoadMore?: () => void;
  hasMore?: boolean;
  isLoading?: boolean;
}

export default function PostFeed({
  posts = [],
  onPostClick,
  onAuthorClick,
  onTagClick,
  onLoadMore,
  hasMore = false,
  isLoading = false
}: PostFeedProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [sortBy, setSortBy] = useState<"latest" | "trending" | "popular">("latest");

  // Get all unique tags from posts
  const allTags = Array.from(new Set(posts.flatMap(post => post.tags)));

  // Filter posts based on search and tags
  const filteredPosts = posts.filter(post => {
    const matchesSearch = searchQuery === "" || 
      post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      post.excerpt.toLowerCase().includes(searchQuery.toLowerCase()) ||
      post.author.name.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesTags = selectedTags.length === 0 ||
      selectedTags.some(tag => post.tags.includes(tag));

    return matchesSearch && matchesTags;
  });

  // Sort posts
  const sortedPosts = [...filteredPosts].sort((a, b) => {
    switch (sortBy) {
      case "trending":
        return (b.stats.likes + b.stats.comments) - (a.stats.likes + a.stats.comments);
      case "popular":
        return b.stats.likes - a.stats.likes;
      default: // latest
        return new Date(b.publishedAt).getTime() - new Date(a.publishedAt).getTime();
    }
  });

  const toggleTag = (tag: string) => {
    setSelectedTags(prev => 
      prev.includes(tag) 
        ? prev.filter(t => t !== tag)
        : [...prev, tag]
    );
    console.log('Tag filter toggled:', tag);
  };

  const clearFilters = () => {
    setSearchQuery("");
    setSelectedTags([]);
    console.log('Filters cleared');
  };

  return (
    <div className="space-y-6">
      {/* Search and Filters */}
      <div className="space-y-4">
        {/* Search Bar */}
        <div className="relative max-w-md">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            data-testid="input-feed-search"
            type="search"
            placeholder="Search posts..."
            className="pl-10"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        {/* Sort Tabs */}
        <Tabs value={sortBy} onValueChange={(value) => setSortBy(value as any)}>
          <TabsList className="grid w-full max-w-md grid-cols-3">
            <TabsTrigger value="latest" data-testid="tab-latest">
              <Clock className="h-4 w-4 mr-2" />
              Latest
            </TabsTrigger>
            <TabsTrigger value="trending" data-testid="tab-trending">
              <TrendingUp className="h-4 w-4 mr-2" />
              Trending
            </TabsTrigger>
            <TabsTrigger value="popular" data-testid="tab-popular">
              <Heart className="h-4 w-4 mr-2" />
              Popular
            </TabsTrigger>
          </TabsList>
        </Tabs>

        {/* Tag Filters */}
        {allTags.length > 0 && (
          <div className="space-y-2">
            <div className="flex items-center gap-2">
              <Filter className="h-4 w-4 text-muted-foreground" />
              <span className="text-sm font-medium">Filter by tags:</span>
              {selectedTags.length > 0 && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={clearFilters}
                  className="text-xs h-6"
                  data-testid="button-clear-filters"
                >
                  Clear all
                </Button>
              )}
            </div>
            <div className="flex flex-wrap gap-2">
              {allTags.slice(0, 12).map((tag) => (
                <Badge
                  key={tag}
                  variant={selectedTags.includes(tag) ? "default" : "secondary"}
                  className="cursor-pointer hover-elevate"
                  onClick={() => toggleTag(tag)}
                  data-testid={`badge-filter-${tag}`}
                >
                  {tag}
                </Badge>
              ))}
            </div>
          </div>
        )}

        {/* Active Filters Summary */}
        {(searchQuery || selectedTags.length > 0) && (
          <div className="text-sm text-muted-foreground">
            Showing {sortedPosts.length} of {posts.length} posts
            {searchQuery && ` matching "${searchQuery}"`}
            {selectedTags.length > 0 && ` with tags: ${selectedTags.join(", ")}`}
          </div>
        )}
      </div>

      {/* Posts Grid */}
      {sortedPosts.length > 0 ? (
        <div className="grid gap-6 md:grid-cols-2 xl:grid-cols-3">
          {sortedPosts.map((post) => (
            <PostCard
              key={post.id}
              post={post}
              onPostClick={onPostClick}
              onAuthorClick={onAuthorClick}
              onTagClick={(tag) => {
                toggleTag(tag);
                onTagClick?.(tag);
              }}
            />
          ))}
        </div>
      ) : (
        <div className="text-center py-12">
          <p className="text-muted-foreground text-lg">No posts found</p>
          <p className="text-sm text-muted-foreground mt-2">
            Try adjusting your search terms or filters
          </p>
        </div>
      )}

      {/* Load More */}
      {hasMore && sortedPosts.length > 0 && (
        <div className="flex justify-center pt-8">
          <Button
            onClick={() => {
              onLoadMore?.();
              console.log('Load more clicked');
            }}
            disabled={isLoading}
            data-testid="button-load-more"
          >
            {isLoading ? "Loading..." : "Load More Posts"}
          </Button>
        </div>
      )}
    </div>
  );
}

